// log
function log( text ) {
	console.log( debugFilter + ": " + text );
}

// logVar
function logVar( variable, string ) {
	if( string !== null ) {
		log( variable + ": " + string );
	} else {
		log( variable + " empty" );
	}
}

// logFunc
function logFunc( functionName ) {
	var seperator = "####################################";
	log( "\n"+ seperator +"\n# "+ functionName +"\n"+ seperator );
}

// logArr
function logArr( name, arr ) {
    log( name + ":\n" );

    $.each( arr, function( index, obj ) {
        log( obj );
    });
}

