// array_unique
// return an array with no duplicates
function array_unique( arrayWithDuplicates ) {
    var uniqueArray = arrayWithDuplicates.filter(function(item, index) {
        return arrayWithDuplicates.indexOf(item) === index;
    });

    return uniqueArray;
}


